/* Code tidied up by ScrapBook */
.bbcode_container { margin-top: 5px; display: block; }
.editor_controls a.popupctrl img, .popupmenu .popupbody li img { vertical-align: middle; }
.popupmenu { position: relative; }
.popupmenu a.popupctrl { background-position: right center; background-repeat: no-repeat; padding-right: 15px; }
.popupbody { background: rgb(229, 228, 202) url("top-highlight.png") repeat-x scroll 0% 0%; border: 1px solid rgb(225, 225, 225); padding: 2px; position: absolute; z-index: 1000; top: 1.5em; left: 0px; display: none; min-width: 200px; }
.popupbody li { border-top: 1px solid rgb(229, 228, 202); }
.popupbody > li:first-child { border-top: medium none; }
.popupbody li > a, .popupbody li > label { display: block; color: rgb(51, 51, 51); padding: 6.235px 10px; background: rgb(229, 228, 202) none repeat-x scroll center bottom; text-decoration: none; text-align: left; white-space: nowrap; font: 11px Tahoma,Calibri,Verdana,Geneva,sans-serif; }
.popupbody li > a:hover, .popupbody li > label:hover, .popupbody li.vbmenu_hilite > a { background-color: rgb(198, 216, 103); color: rgb(34, 34, 34); }
.popupbody li.formsubmit { text-align: right; background: transparent none repeat scroll 0% 0%; }
.popupbody li.formsubmit.jumptopage > label { display: inline; background: transparent none repeat scroll 0% 0%; }
.popupbody li.formsubmit input[type="submit"], .popupbody li.formsubmit input[type="reset"], .popupbody li.formsubmit input[type="button"] { font: 9pt Tahoma,Calibri,Verdana,Geneva,sans-serif; padding: 2px 2px 1px; }
.popupbody li.formsubmit input[type="submit"]:hover, .popupbody li.formsubmit input[type="reset"]:hover, .popupbody li.formsubmit input[type="button"]:hover { color: rgb(65, 115, 148); }
.memberaction.popupmenu { position: relative; top: 0px; display: inline-block; }
.memberaction.popupmenu a.popupctrl { background: transparent none repeat scroll 0% 0%; padding: 0px; }
.memberaction_body.popupbody { width: 280px; background: rgb(229, 228, 202) none repeat scroll 0% 0%; border-radius: 5px; box-shadow: 0px 4px 7px; position: fixed; }
.memberaction_body.popupbody li { border: medium none; width: 140px; }
.memberaction_body.popupbody li.left { float: left; clear: left; }
.memberaction_body.popupbody li.right { float: right; clear: right; }
.memberaction_body.popupbody li img { float: left; padding: 4px; position: relative; top: -2px; }
.memberaction_body.popupbody li > a { padding: 4px 4px 4px 20px; }
.button { padding: 2px; }
.button:hover { color: rgb(65, 115, 148); }
.textbox, textarea, select { font: 11px Tahoma,Calibri,Verdana,Geneva,sans-serif; color: rgb(0, 0, 0); background: rgb(255, 255, 255) none repeat scroll 0% 0%; padding: 4px; outline: 0px none; border-radius: 3px; }
